import React, { Component, useState } from 'react';

const [tilte, setTitle] = useState("");
const [subtilte, setSubtitle] = useState("");
const [description, setDescription] = useState("");
const [dateP, setDateP] = useState("");
const [dateE, setDateE] = useState("");

const collectData = async (event) =>{
  event.preventDefault();
  var result = await fetch('http://localhost:8080/create', {
    method: 'POST',
    body: JSON.stringify({tilte, subtilte, description, dateP, dateE}),
    header: {
      'content-Type' : 'application/json',
      'Accept': 'application/json'
    }
  })
  result = await result.json;
  localStorage.setItem("Task", JSON.stringify(result));
}

