import React, { Component, useState } from 'react';

export default class Task extends Component {
  
  render() {
    const [tilte, setTitle] = useState("");
    const [subtilte, setSubtitle] = useState("");
    const [image, setImage] = useState("");
    const [description, setDescription] = useState("");
    const [dateP, setDateP] = useState("");
    const [dateE, setDateE] = useState("");
    
    const collectData = async (e) =>{
      try {
        e.preventDefault();
        var result = await fetch('http://localhost:8080/', {
          method: 'POST',
          body: JSON.stringify({tilte, subtilte, image, description, dateP, dateE}),
          header: {'content-Type' : 'application/json'}
        })
        result = await result.json;
        localStorage.setItem("Task", JSON.stringify(result));
      } catch(error) {

      }
      
    }
    return (
      <div className='container col-5 text-center rounded position-absolute top-50 start-50 translate-middle p-3 bg-light'>
        <form onSubmit={collectData}>
          <div className='row'>
            <div className='col-5'>
              <input className="form-control" type="file" id="formFile" accept='.png, .jpg, .jpeg' 
              value={image} onChange={(e) => setImage(e.target.value)} />
            </div>
          </div>
          <div className='row mt-4'>
            <div className='col-4'>
              <input type="text" className="form-control" id="exampleFormControlInput1" placeholder="Title" 
              value={tilte} onChange={(e) => setTitle(e.target.value)}/>
            </div>
          </div>
          <div className='row mt-2'>
              <div className='col-5'>
                <input type="text" className="form-control" id="exampleFormControlInput1" placeholder="Subtitle" 
                value={subtilte} onChange={(e) => setSubtitle(e.target.value)} />
              </div>
          </div>
          <div className='row mt-2'>
              <div className='col-6'>
                <textarea class="form-control" id="exampleFormControlTextarea1" placeholder='description' 
                value={description} onChange={(e) => setDescription(e.target.value)}/>
              </div>
          </div>
          <div className='row mt-2'>
              <div className='col-2 d-flex align-items-center'>
                <label for="exampleFormControlInput1" class="form-label">Start date</label>
              </div>
              <div className='col-4'>
                <input type="date" id="exampleFormControlInput1" className="form-control" 
                value={dateP} onChange={(e) => setDateP(e.target.value)}/>
              </div>
          </div>
          <div className='row mt-2'>
            <div className='col-2 d-flex align-items-center'>
              <label for="exampleFormControlInput1" class="form-label">End date</label>
            </div>
              <div className='col-4'>
                <input type="date" id="exampleFormControlInput1" className="form-control" 
                value={dateE} onChange={(e) => setDateE(e.target.value)}/>
              </div>
          </div>
          <div className='row mt-5 justify-content-md-center'>
            <div className='col-2'>
              <button type="submit" className="btn btn-primary">Submit</button>
            </div>
          </div> 
        </form>       
      </div>
    );
  }
}
