import React from 'react';
import backgroundImage from '../backgroundImage.jpg'

function SignInForm() {
    // const [username, setUsername] = useState("");
    // const [password, setPassword] = useState("");

    // async function configuration(event) {
    //     event.preventDefault();

    //     axios.post('http://localhost:8080/', {username, password})
    // }

    return (
        <div className="vh-100 gradient-custom" style={{backgroundImage: backgroundImage}, {height: "300px"}}>
            <div className="container py-5 h-100">
                <div className="row d-flex justify-content-center align-items-center h-100">
                <div className="col-12 col-md-8 col-lg-6 col-xl-5 mb-4">
                    <div className="card bg-light text-white">
                    <div className="card-body p-5 text-center">

                        <div className="mb-md-5 mt-md-4 pb-5">

                        <h2 className="fw-bold mb-2 text-dark">Login</h2>
                        <p className="text-white-50 mb-5">Please enter your login and password!</p>

                        <div data-mdb-input-init className="form-outline form-white mb-4">
                            <label className="form-label text-dark" for="typeEmailX">Email</label>
                            <input type="email" id="typeEmailX" className="form-control form-control-lg" />
                        </div>
                        <div data-mdb-input-init className="form-outline form-white mb-4">
                            <label className="form-label text-dark" for="typePasswordX">Password</label>
                            <input type="password" id="typePasswordX" className="form-control form-control-lg" />
                        </div>
                        <p className="small mb-5 pb-lg-2 "><a className="text-dark-50 " href="#!">Forgot password?</a></p>

                        <button className="btn btn-outline-primary btn-lg px-5" type="submit">Login</button>

                        </div>

                        <div>
                        <p className="mb-0 text-dark">Don't have an account? <a href="/registration" className="text-dark-50 fw-bold">Sign Up</a>
                        </p>
                        </div>

                    </div>
                    </div>
                </div>
                </div>
            </div>
        </div>      
    )
}

export default SignInForm;
