import axios from 'axios';
import React, { useState } from 'react';

function SignInForm() {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");

    async function configuration(event) {
        event.preventDefault();

        axios.post('http://localhost:8080/', {username, password})
    }

    return (
        <div className='Singin'>
            <div className='container'>
                <form method='POST'>
                    <div>
                        <input type='text' className='' value={username} onChange={event => setUsername(event.target.value)}/>
                    </div>
                    <div>
                        <input type='password' cla value={password} onChange={event => setPassword(event.target.value)}/>
                    </div>
                    <div>
                        <button type='submit' onClick={configuration}>Log In</button>
                    </div>
                </form>
            </div>
        </div>
    )
}

export default SignInForm;
